import { filter, map, tap } from 'rxjs';
import { SCORE_ACTION } from '../topo/topo.enum';
export function tocarAlTopo(nativeElement) {
  return function (source) {
    return source.pipe(filter(event => event.isTrusted), tap(() => {
      if (nativeElement.parentElement) {
        nativeElement.parentElement.classList.remove('up');
      }
    }), map(() => SCORE_ACTION.ADD));
  };
}
