import { concatMap, scan, startWith, take, timer } from 'rxjs';
export function tiempoJuego(gameDurationInSeconds = 10) {
  return function (source) {
    return source.pipe(concatMap(() => timer(0, 1000).pipe(take(gameDurationInSeconds), scan(acc => acc - 1, gameDurationInSeconds))), startWith(gameDurationInSeconds));
  };
}
